import{_ as t,c as l,a0 as e,o as i}from"./chunks/framework.OqxnQCTf.js";const p=JSON.parse('{"title":"ChatGPT","description":"","frontmatter":{},"headers":[],"relativePath":"docs/深度学习/李宏毅/2023年/1.chatGPT.md","filePath":"docs/深度学习/李宏毅/2023年/1.chatGPT.md"}'),o={name:"docs/深度学习/李宏毅/2023年/1.chatGPT.md"};function r(c,a,h,n,d,s){return i(),l("div",null,a[0]||(a[0]=[e('<h1 id="chatgpt" tabindex="-1">ChatGPT <a class="header-anchor" href="#chatgpt" aria-label="Permalink to &quot;ChatGPT&quot;">​</a></h1><p><code>ChatGPT</code>不是从网络中获取数据进行问题的回答，本质是一种文字接龙游戏，会从随机区域中获取概率最大的数字进行拼凑</p><ul><li>训练：可以比喻为你在考试前的疯狂复习，将知识装入大脑中</li><li>测试：可以比喻为你在考试，根据题目进行回答</li></ul><h2 id="chatgpt的关键技术-预训练-pre-train" tabindex="-1">ChatGPT的关键技术：预训练（Pre-train) <a class="header-anchor" href="#chatgpt的关键技术-预训练-pre-train" aria-label="Permalink to &quot;ChatGPT的关键技术：预训练（Pre-train)&quot;">​</a></h2><blockquote><p>自督导式学习 基石模型</p></blockquote><p>网络的每一段文字都可以进行训练，教机器做文字接龙</p><ul><li>2018年的GPT-1 =&gt; Model 117M个参数</li><li>2019年的GPT-2 =&gt; Model 1542M个参数</li><li>2020年的GPT-3 =&gt; Model 175B个参数 训练资料Data 570GB</li></ul><p>GPT-&gt; （预训练 自督导式学习） -&gt; ChatGPT</p><blockquote><p>人工就是督导式学习，不用人工产生的资料就是自督导式学习</p></blockquote><ul><li>督导式学习：需要提供出正确的答案</li><li>增强式学习：只要👍和👎，给答案进行反馈对/错即可</li></ul><h2 id="研究方向" tabindex="-1">研究方向 <a class="header-anchor" href="#研究方向" aria-label="Permalink to &quot;研究方向&quot;">​</a></h2><ul><li>如何更准确提出需求</li><li>如何修正GPT的错误答案</li><li>如何检验，区分出GPT和人工的文字</li></ul><h2 id="社会化发展方向" tabindex="-1">社会化发展方向 <a class="header-anchor" href="#社会化发展方向" aria-label="Permalink to &quot;社会化发展方向&quot;">​</a></h2><ul><li>依靠网络数据进行学习</li><li>GPT提供问题，人工给出答案（只需要少量答案就可以很好修正GPT的答案，主要是让GPT明白人们想要什么答案）</li></ul>',14)]))}const u=t(o,[["render",r]]);export{p as __pageData,u as default};
