import{_ as e,c as t,a0 as l,o as i}from"./chunks/framework.OqxnQCTf.js";const p=JSON.parse('{"title":"生成式AI","description":"","frontmatter":{"outline":[1,6]},"headers":[],"relativePath":"深度学习/李宏毅/2023年&2024年/2.生成式AI.md","filePath":"深度学习/李宏毅/2023年&2024年/2.生成式AI.md"}'),o={name:"深度学习/李宏毅/2023年&2024年/2.生成式AI.md"};function r(c,a,n,s,d,h){return i(),t("div",null,a[0]||(a[0]=[l('<h1 id="生成式ai" tabindex="-1">生成式AI <a class="header-anchor" href="#生成式ai" aria-label="Permalink to &quot;生成式AI&quot;">​</a></h1><p>机器学习= 机器自动从资料中找一个函数</p><ol><li>深度学习是一种机器学习技术</li><li>类神经网络就是一个有大量参数的函数</li><li><code>Transformer</code> 是类神经网络的一种</li></ol><h2 id="根据函数输出来分类" tabindex="-1">根据函数输出来分类 <a class="header-anchor" href="#根据函数输出来分类" aria-label="Permalink to &quot;根据函数输出来分类&quot;">​</a></h2><ul><li>回归（Regression)：函数的输出是一个数值，比如预测明天的气温是多少度</li><li>分类（Classification)：函数的输出是一个类别（选择题），比如是不是数码宝贝</li><li>结构化学习（Structured Learning）：也叫生成式学习，生成有结构的物件（比如 图片、句子等等）</li></ul><h2 id="chatgpt是哪一类机器学习" tabindex="-1">ChatGPT是哪一类机器学习？ <a class="header-anchor" href="#chatgpt是哪一类机器学习" aria-label="Permalink to &quot;ChatGPT是哪一类机器学习？&quot;">​</a></h2><ul><li>分类：给一个句子，然后决定可能要接的哪一个字，因此可以认为ChatGPT是&quot;分类&quot;</li><li>生成式学习：对于使用者感受来说，就是不断生成文字的过程，因此可以认为它是一个生成式学习，只不过它的生成式是拆分为多个分类问题（每一个字都是分类问题）</li></ul><h2 id="找出函数的三个步骤" tabindex="-1">找出函数的三个步骤 <a class="header-anchor" href="#找出函数的三个步骤" aria-label="Permalink to &quot;找出函数的三个步骤&quot;">​</a></h2><blockquote><p>前置操作：决定要做什么类型的应用=&gt;找什么样的函数</p></blockquote><ul><li><p>设定范围：在多个Model模型中（比如Transform、CNN）中选择出多个模型</p></li><li><p>设定标准：设置model输出的评分标准，比如下图，我们可以根据测试数据得到差值 <img src="https://github.com/user-attachments/assets/b3e4a99f-55f6-402d-a4aa-eef3a6c1f893" alt="image"></p></li><li><p>达成目标：使用<code>Gradient Descent</code>或者其他算法，不断收缩范围得到一个最合适的值</p></li></ul><blockquote><p><code>Gradient Descent</code>可以参考2021&amp;2022年的视频，就是一个线性斜率构建向量不断比较的过程</p></blockquote>',11)]))}const m=e(o,[["render",r]]);export{p as __pageData,m as default};
